!function ($) {
    var css = `.toast-content{position: relative;},.toaster-title{font-weight:700}.toaster-message{-ms-word-wrap:break-word;word-wrap:break-word}.toaster-message a,.toaster-message label{color:#FFF}.toaster-message a:hover{color:#CCC;text-decoration:none}.toaster-close-button{position:relative;right:-.3em;top:-.3em;float:right;font-size:20px;font-weight:700;color:#FFF;-webkit-text-shadow:0 1px 0 #fff;text-shadow:0 1px 0 #fff;opacity:1;-ms-filter:progid:DXImageTransform.Microsoft.Alpha(Opacity=80);filter:alpha(opacity=80);line-height:1}.toaster-close-button:focus,.toaster-close-button:hover{color:#000;text-decoration:none;cursor:pointer;opacity:1;-ms-filter:progid:DXImageTransform.Microsoft.Alpha(Opacity=40);filter:alpha(opacity=40)}.rtl .toaster-close-button{left:-.3em;float:left;right:.3em}button.toaster-close-button{padding:0;cursor:pointer;background:0 0;border:0;-webkit-appearance:none}.toaster-top-center{top:0;right:0;width:100%}.toaster-bottom-center{bottom:0;right:0;width:100%}.toaster-top-full-width{top:0;right:0;width:100%}.toaster-bottom-full-width{bottom:0;right:0;width:100%}.toaster-top-left{top:12px;left:12px}.toaster-top-right{top:12px;right:12px}.toaster-bottom-right{right:12px;bottom:12px}.toaster-bottom-left{bottom:12px;left:12px}#toaster-container{position:fixed;z-index:999999;pointer-events:none}#toaster-container *{-moz-box-sizing:border-box;-webkit-box-sizing:border-box;box-sizing:border-box}#toaster-container>div{position:relative;pointer-events:auto;overflow:hidden;margin:0 0 6px;padding:15px 15px 15px 50px;width:300px;-moz-border-radius:3px;-webkit-border-radius:3px 3px 3px 3px;border-radius:3px;background-position:15px center;background-repeat:no-repeat;-moz-box-shadow:0 0 12px #999;-webkit-box-shadow:0 0 12px #999;box-shadow:0 0 12px #999;color:#FFF;opacity:1;-ms-filter:progid:DXImageTransform.Microsoft.Alpha(Opacity=100);filter:alpha(opacity=100)}#toaster-container>div.rtl{direction:rtl;padding:15px 50px 15px 15px;background-position:right 15px center}#toaster-container>div:hover{-moz-box-shadow:0 0 12px #000;-webkit-box-shadow:0 0 12px #000;box-shadow:0 0 12px #000;opacity:1;-ms-filter:progid:DXImageTransform.Microsoft.Alpha(Opacity=100);filter:alpha(opacity=100);cursor:pointer}#toaster-container>.toaster-info{background-image:url(data:image/png;base64,)!important}#toaster-container>.toaster-error{background-image:url(data:image/png;base64,)!important}#toaster-container>.toaster-success{background-image:url(data:image/png;base64,)!important}#toaster-container>.toaster-warning{background-image:url(data:image/png;base64,)!important}#toaster-container.toaster-bottom-center>div,#toaster-container.toaster-top-center>div{width:300px;margin-left:auto;margin-right:auto}#toaster-container.toaster-bottom-full-width>div,#toaster-container.toaster-top-full-width>div{width:96%;margin-left:auto;margin-right:auto}.toaster{background-color:#030303}.toaster-success{background-color:#51A351}.toaster-error{background-color:#BD362F}.toaster-info{background-color:#2F96B4}.toaster-warning{background-color:#F89406}.toaster-progress{position:absolute;left:0;bottom:0;height:4px;background-color:#000;opacity:.4;-ms-filter:progid:DXImageTransform.Microsoft.Alpha(Opacity=40);filter:alpha(opacity=40)}@media all and (max-width:240px){#toaster-container>div{padding:8px 8px 8px 50px;width:11em}#toaster-container>div.rtl{padding:8px 50px 8px 8px}#toaster-container .toaster-close-button{right:-.2em;top:-.2em}#toaster-container .rtl .toaster-close-button{left:-.2em;right:.2em}}@media all and (min-width:241px) and (max-width:480px){#toaster-container>div{padding:8px 8px 8px 50px;width:18em}#toaster-container>div.rtl{padding:8px 50px 8px 8px}#toaster-container .toaster-close-button{right:-.2em;top:-.2em}#toaster-container .rtl .toaster-close-button{left:-.2em;right:.2em}}@media all and (min-width:481px) and (max-width:768px){#toaster-container>div{padding:15px 15px 15px 50px;width:18em}#toaster-container>div.rtl{padding:15px 50px 15px 15px}}`;
    $('head').append('<style>' + css + '</style>')
    $.toast = function () {
        if ($('.toast-content').find('#toaster-container').length == 0) {
            var mainContainer = $('<div id="toaster-container" class="toaster-top-right"></div>');
            mainContainer.appendTo('.toast-content');
        }
        var mainContainer = $('#toaster-container');
        var toasterContainer = $('<div>');
        var tostr = {};

        var defaultOption = {
            hideOnClick: true,
            showCloseIcon: true,
            onHide: function () {},
            timeOut: 5000,
            static:false
        };

        var getObject = function(){
            return toasterContainer;
        };
        var success = function (message, title, options) {
            setContent(message, title, 'success', options);
            return tostr;
        };
        var info = function (message, title, options) {
            setContent(message, title, 'info', options);
            return tostr;
        };
        var error = function (message, title, options) {
            setContent(message, title, 'error', options);
            return tostr;
        };
        var warning = function (message, title, options) {
            setContent(message, title, 'warning', options);
            return tostr;
        };
        var setContent = function (txtMssage, txtTitle, type, options) {
            if (typeof options == 'undefined')
                options = {};
            options = $.extend(defaultOption, options);
            toasterContainer.addClass('toaster').addClass("toaster-" + type).attr('arial-live');
            if (options.showCloseIcon) {
                var closeButton = $('<button>').addClass('toaster-close-button').attr('role', 'button').html('x');
                toasterContainer.append(closeButton);
                closeButton.click(remove);
            }
            if (options.hideOnClick) {
                toasterContainer.click(remove);
            }
            var title = $('<div>').addClass('toaster-title').html(escapeHtml(txtTitle));
            toasterContainer.append(title);
            var message = $('<div>').addClass('toaster-message').html((txtMssage));
            toasterContainer.append(message);
            mainContainer.prepend(toasterContainer);
            if(!options.static){
                setTimeout(remove,options.timeOut);
            }
        };
        var remove = function () {
            toasterContainer.fadeOut(300, function () {
                $(this).remove();
                defaultOption.onHide();
            });
        };
        tostr = {
            success: success,
            info: info,
            warning: warning,
            error: error,
            remove: remove,
            getObject:getObject
        };
        function escapeHtml(source) {
            if (source == null) {
                source = '';
            }

            return source
                    .replace(/&/g, '&amp;')
                    .replace(/"/g, '&quot;')
                    .replace(/'/g, '&#39;')
                    .replace(/</g, '&lt;')
                    .replace(/>/g, '&gt;');
        }
        return tostr;
    };
}($);
